#!/usr/bin/env python3# -*- coding: utf-8 -*-# Copyright(C) 2001 - 2023 SUZUKI Hisao, Mitko Haralanov, Åukasz Langa, Seven Ling# Permission is hereby granted, free of charge, to any person obtaining a copy# of this software and associated documentation files(the "Software"), to deal# in the Software without restriction, including without limitation the rights# to use, copy, modify, merge, publish, distribute, sublicense, and/or sell# copies of the Software, and to permit persons to whom the Software is# furnished to do so, subject to the following conditions:# The above copyright notice and this permission notice shall be included in# all copies or substantial portions of the Software.# THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR# IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY,# FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE# AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER# LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM,# OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS IN# THE SOFTWARE.__version__ = "1.2"from http.server import BaseHTTPRequestHandler, HTTPServerimport errnoimport ftplibimport loggingimport logging.handlersimport osimport reimport selectorsimport socketimport socketserverimport sysimport threadingfrom time import sleepimport urllib.parseimport argparseHEADER_TERMINATOR = re.compile(r'\r\n\r\n')class ProxyHandler(BaseHTTPRequestHandler):    server_version = "TinyHTTPProxy/" + __version__    protocol = "HTTP/1.0"    rbufsize = 0  # self.rfile Be unbuffered    allowed_clients = ()    cache = False    def handle(self):        ip, port = self.client_address        self.server.logger.log(logging.DEBUG, "Request from '%s'", ip)        if self.allowed_clients and ip not in self.allowed_clients:            self.raw_requestline = self.rfile.readline()            if self.parse_request():                self.send_error(403)        else:            BaseHTTPRequestHandler.handle(self)    def _connect_to(self, netloc, soc):        i = netloc.find(':')        if i >= 0:            host_port = netloc[:i], int(netloc[i + 1:])        else:            host_port = netloc, 80        self.server.logger.log(            logging.DEBUG, "Connect to %s:%d", host_port[0], host_port[1])        try:            soc.connect(host_port)        except socket.error as arg:            self.send_error(500, f'Connect to {netloc} error')            return False        return True    def do_CONNECT(self):        self.server.logger.debug(f'Handle request to url[{self.path}]')        soc = socket.socket(socket.AF_INET, socket.SOCK_STREAM)        try:            if self._connect_to(self.path, soc):                self.log_request(200)                self.wfile.write((self.protocol_version +                                  " 200 Connection established\r\n").encode())                self.wfile.write(("Proxy-agent: %s\r\n" % self.version_string()).encode())                self.wfile.write("\r\n".encode())                self._read_write(soc, 300)        finally:            soc.close()            self.connection.close()    def do_GET(self):        self.server.logger.debug(f'Handle request to url[{self.path}]')        scm, netloc, path, params, query, fragment = urllib.parse.urlparse(            self.path, 'http')        if scm not in ('http', 'ftp') or fragment or not netloc:            self.send_error(400, "bad URL %s" % self.path)            return        soc = socket.socket(socket.AF_INET, socket.SOCK_STREAM)        try:            if scm == 'http':                if self._connect_to(netloc, soc):                    self.log_request()                    rq = []                    rq.append("%s %s %s\r\n" % (                        self.command, urllib.parse.urlunparse(                            ('', '', path, params, query, '')),                        self.request_version,                    ))                    self.headers['Connection'] = 'close'                    del self.headers['Proxy-Connection']                    for key_val in list(self.headers.items()):                        self.log_verbose("%s: %s", *key_val)                        rq.append("%s: %s\r\n" % key_val)                    rq.append("\r\n")                    soc.send(''.join(rq).encode())                    self._read_write(soc, 300)            elif scm == 'ftp':                # fish out user and password information                i = netloc.find('@')                if i >= 0:                    login_info, netloc = netloc[:i], netloc[i + 1:]                    try:                        user, passwd = login_info.split(':', 1)                    except ValueError:                        user, passwd = "anonymous", None                else:                    user, passwd = "anonymous", None                self.log_request()                try:                    ftp = ftplib.FTP(netloc)                    ftp.login(user, passwd)                    if self.command == "GET":                        ftp.retrbinary("RETR %s" % path, self.connection.send)                    ftp.quit()                except Exception as e:                    self.server.logger.log(                        logging.WARNING, "FTP Exception: %s", e                    )        finally:            soc.close()            self.connection.close()    def handle_one_request(self):        try:            BaseHTTPRequestHandler.handle_one_request(self)        except socket.error as e:            if e.errno == errno.ECONNRESET:                pass  # ignore the error            else:                raise    def _read_write(self, soc, max_idling=20):        sel = selectors.DefaultSelector()        def open_io():            sel.register(self.connection, selectors.EVENT_READ, data="client")            sel.register(soc, selectors.EVENT_READ, data="server")        def close_io():            for i in [self.connection, soc]:                sel.unregister(i)            sel.close()        open_io()        count = 0        while True:            count += 1            events = sel.select(0.1)            for key, event in events:                data = key.fileobj.recv(4096)                if key.fileobj == soc:                    out = self.connection                else:                    out = soc                if data:                    try:                        out.sendall(data)                    except:                        if out == soc:                            # Send to server error                            self.log_error('Send data to server error')                            self.send_error(500, 'Upstream has gone away')                        else:                            # Send to client error                            self.log_error('Send data to client error')                        close_io()                        return False                    count = 0                else:                    self.server.logger.debug(f'Recv close event from {key.data}')                    close_io()                    return True            if count == max_idling:                break        close_io()        return True    do_OPTION = do_GET    do_HEAD = do_GET    do_POST = do_GET    do_PUT = do_GET    do_DELETE = do_GET    def log_verbose(self, fmt, *args):        self.server.logger.log(            logging.DEBUG, "%s %s", self.address_string(), fmt % args        )    def log_message(self, fmt, *args):        self.server.logger.log(            logging.INFO, "%s %s", self.address_string(), fmt % args        )    def log_error(self, fmt, *args):        self.server.logger.log(            logging.ERROR, "%s %s", self.address_string(), fmt % args        )class ThreadingHTTPServer(socketserver.ThreadingMixIn, HTTPServer):    def __init__(self, server_address, RequestHandlerClass, logger=None):        HTTPServer.__init__(self, server_address, RequestHandlerClass)        self.logger = loggerdef setup_logging(filename, log_size, daemon, verbose):    logger = logging.getLogger("TinyHTTPProxy")    logger.setLevel(logging.DEBUG if verbose else logging.INFO)    if not filename or filename in ('-', 'STDOUT'):        # display to the screen        handler = logging.StreamHandler()    else:        handler = logging.handlers.RotatingFileHandler(            filename, maxBytes=(log_size * (1 << 20)), backupCount=5)    fmt = logging.Formatter("[%(asctime)-12s.%(msecs)03d] "                            "%(levelname)-8s %(threadName)s  "                            "%(message)s",                            "%Y-%m-%d %H:%M:%S")    handler.setFormatter(fmt)    logger.addHandler(handler)    return loggerdef daemonize(logger):    class DevNull(object):        def __init__(self):            self.fd = os.open(os.devnull, os.O_WRONLY)        def write(self, *args, **kwargs):            return 0        def read(self, *args, **kwargs):            return 0        def fileno(self):            return self.fd        def close(self):            os.close(self.fd)    class ErrorLog(object):        def __init__(self, obj):            self.obj = obj        def write(self, string):            self.obj.log(logging.ERROR, string)        def read(self, *args, **kwargs):            return 0        def close(self):            pass    if os.fork() != 0:        # allow the child pid to instantiate the server class        sleep(1)        sys.exit(0)    os.setsid()    fd = os.open(os.devnull, os.O_RDONLY)    if fd != 0:        os.dup2(fd, 0)        os.close(fd)    null = DevNull()    log = ErrorLog(logger)    sys.stdout = null    sys.stderr = log    sys.stdin = null    fd = os.open(os.devnull, os.O_WRONLY)    os.dup2(sys.stdout.fileno(), 1)    if fd != 2:        os.dup2(fd, 2)    if fd not in (1, 2):        os.close(fd)def usage():    parser = argparse.ArgumentParser(description='Tiny HTTP Proxy')    parser.add_argument('-H', '--host', type=str, dest="host", default='127.0.0.1',                        help="Host to bind to [default: 127.0.0.1]")    parser.add_argument('-P', '--port', type=int, dest="port", default='8000',                        help="Port to bind to [default: 8000]")    parser.add_argument('-l', '--logfile', type=str, dest="logfile", default='STDOUT',                        help="Path to the logfile [default: STDOUT]")    parser.add_argument('-d', '--daemon', action='store_true', dest="daemon", default=False,                        help="Daemonize (run in the background). Daemon mode must specify logfile")    parser.add_argument('-v', '--verbose', action='store_true', dest="verbose", default=False,                        help="Log debug info")    args = parser.parse_args()    if args.daemon and args.logfile == 'STDOUT':        parser.print_help()        sys.exit(1)    return args.host, args.port, args.logfile, args.daemon, args.verbosedef main():    host, port, logfile, daemon, verbose = usage()    if not (0 < port < 65536):        print(f'Port[{port}] invalid')        return False    # Init logger    logger = setup_logging(logfile, 20, daemon, verbose)    # Setup daemon    if daemon:        daemonize(logger)    server_address = socket.gethostbyname(host), port    httpd = ThreadingHTTPServer(server_address, ProxyHandler, logger)    sa = httpd.socket.getsockname()    logger.info("Serving HTTP on %s:%s" % (sa[0], sa[1]))    req_count = 0    while True:        try:            httpd.handle_request()            req_count += 1            if req_count == 1000:                logger.log(                    logging.INFO, "Number of active threads: %s",                    threading.activeCount()                )                req_count = 0        except Exception as e:                logger.log(logging.ERROR, f"Handle request error: {e}")        except KeyboardInterrupt:            logger.log(logging.INFO, 'Recive exit signal, process exit')            break    return 0if __name__ == '__main__':    sys.exit(main())